class Solution(object):
    def sumHalf(self, arr):
        sum = 0
        for n in arr:
            sum += n
        return sum

    def pivotIndex(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        no_answer = -1
        r_sum = None
        l_sum = None
        for i, n in enumerate(nums):
            if l_sum is None:
                # l_sum at first index is always zero
                l_sum = 0
            else:
                prev_l_sum = l_sum
                l_sum = prev_l_sum + nums[i-1]
            if r_sum is None:
                r_sum = self.sumHalf(nums[i + 1:])
            else:
                prev_r_sum = r_sum
                r_sum = prev_r_sum - n
            if l_sum == r_sum:
                return i
        # if nothing else is found, return -1
        return no_answer


